    private string _account;
    private string _contract;

    private string _chain = "ethereum";
    private string _network = "rinkeby";

    public CryptoDronesContract(string account, string contract)
    {
        if (account.Length != 42)
        {
            throw new System.Exception("Invalid account");
        }

        if (contract.Length != 42)
        {
            throw new System.Exception("Invalid contract");
        }

        _account = account;
        _contract = contract;
    }

    async public Task<List<CryptoDrones.DroneAttributes>> GetDrones()
    {
        var balance = await ERC721.BalanceOf(_chain, _network, _contract, _account);

        var tasks = Enumerable.Range(0, balance).Select(i => GetAccountDrone(i));
        var drones = await Task.WhenAll(tasks);

        return drones.ToList();
    }

    async public Task<CryptoDrones.DroneAttributes> GetAccountDrone(int index)
    {
        var droneId = await _tokenOfOwnerByIndex(_account, index);
        return await _getDrone(droneId);
    }

    private async Task<int> _tokenOfOwnerByIndex(string account, int index)
    {
        return await CallContract<int>("tokenOfOwnerByIndex", account, index);
    }

    private async Task<CryptoDrones.DroneAttributes> _getDrone(int droneId)
    {
        return await CallContract<CryptoDrones.DroneAttributes>("getDrone", droneId);
    }

    [Serializable]
    private class Wrapper<T>
    {
        public T Value;
    }

    private async Task<T> CallContract<T>(string method, params object[] args)
    {
        var response = await EVM.Call(_chain, _network, _contract, _abi, method, ToString(args));
        Debug.Log($"CallContract response = {response}.");
        return JsonConvert.DeserializeObject<Wrapper<T>>("{\"Value\":" + response + "}").Value;
    }


    private string ToString(object[] args)
    {
        Debug.Log(JsonConvert.SerializeObject(args));
        var items = args.Select(x =>
        {
            if (x is string)
            {
                return "\"" + x + "\"";
            }
            else
            {
                return x.ToString();
            }
        }).Aggregate((x, y) => x + "," + y);

        return "[" + items + "]";
    }
